    private void btnMediaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnMediaActionPerformed
        // TODO add your handling code here:
        
        double av1,av2,media;
        String status;
        
        av1=Double.parseDouble(txtAV1.getText());
        av2=Double.parseDouble(txtAV2.getText());
        media=(av1+av2)/2;
        
        if(media>=6){
            status="Aluno Aprovado";
        }else {
            status="Aluno Reprovado";
            }
        lblMedia.setText("media igual a "+media);
        lblStatus.setText(status);
        
        
    }//GEN-LAST:event_btnMediaActionPerformed

